import React, { useState } from 'react'
import { Query } from 'react-apollo'
import { gql } from 'apollo-boost'

const ALL_AUTHORS = gql`
{
  allAuthors {
    name
    born
    bookCount
  }
}
`

const Authors = (props) => {
  if (!props.show) {
    return null
  }
  let authors = []

  return <Query query={ALL_AUTHORS}>
    {(result) => {
      if(result.loading) {
        return <div>Loading...</div>
      }

      console.log(result)
      authors = authors.concat(result.data.allAuthors)
      return (
        <div>
          <h2>authors</h2>
          <table>
            <tbody>
              <tr>
                <th></th>
                <th>
                  born
                </th>
                <th>
                  books
                </th>
              </tr>
              {authors.map(a =>
                <tr key={a.name}>
                  <td>{a.name}</td>
                  <td>{a.born}</td>
                  <td>{a.bookCount}</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )
    }}
  </Query>

}

export default Authors
